	cin >> n;
	for (int i = 0;i < n;i++) {
		int a;
		cin >> a;
		arr.push_back(a);
	}
	sort(arr.begin(), arr.end());
	for (int i = 0;i < n;i++) {
		cout << arr[i] << " ";
	}
