#include <iostream>
#include <vector>
using namespace std;
int partition(vector <int>&arr, int low, int high) {
	int base = arr[low];
	int i = low + 1, j = high;
	while (i <= j) {
		while ( i <= j && arr[i] <= base) {
			i++;
		}
		while (i <= j && arr[j] >= base) {
			j--;
		}
		if (i < j) {
			swap(arr[i], arr[j]);
			i++;
			j--;
		}
	}
	swap(arr[low], arr[j]);
	return i;
}
void quicksort(vector <int>& arr, int low, int high) {
	if (low >= high)return;
	int pi = partition(arr, low, high);
	quicksort(arr, low, pi-1);
	quicksort(arr, pi+1, high);
}
int main() {
	vector <int> R;
	int N;
	cin >> N;
	for (int i = 0;i < N;i++) {
		int a;
		cin >> a;
		R.push_back(a);
	}
	quicksort(R, 0, N - 1);
	for (int i = 0;i < N;i++) {
		cout << R[i] << " ";
	}
	return 0;
}
```
