class Solution {
public:
    int hIndex(vector<int>& citations) {
        int n=citations.size();
        sort(citations.begin(),citations.end());
        int index;
        for(int i=0;i<n;i++){
            if(n-i>=citations[i]){
                index=citations[i];
            }
        }
        return index;
    }
};
