class Solution {
public:
    void moveZeroes(vector<int>& nums) {
        int n=nums.size();
        int count=0;
        for(int i=0;i<n-1;i++){
            if(nums[i]==0){
                count++;
                for(int j=i;j<n-1;j++){
                    nums[j]=nums[j+1];
