#include <iostream>
#include <string>
using namespace std;
int countnumber(string s) {
	int n = s.size();
	int num=0;
	for (int i = 0;i < n;i++) {
		int left = i;
		int right = i;

		while (left >= 0 && right < n && s[left] == s[right]) {
			num++;
			--left;
			++right;
		}
		left = i, right = i + 1;
		while (left >= 0 && right < n && s[left] == s[right]) {
			num++;
			--left;
			++right;
		}
	}
	return num;
}
int main() {
	string s;
	while (cin >> s) {
	cout << countnumber(s) << endl;
}
	return 0;
}
