#include <iostream>
using namespace std;

void predictNext(int a, int b, int c, int d, int e) {
    if (a == 0 && b == 0 && c == 0 && d == 0 && e == 0) return;

    int d = b - a;
    if (b + d == c && c + d == d && d + d == e) {
        cout << e + d << " " << e + 2 * d << " " << e + 3 * d
            << " " << e + 4 * d << " " << e + 5 * d << endl;
        return;
    }

    if (a != 0 && b != 0 && c != 0 && d != 0 && e != 0) {
        int q = b / a;
        if (a * q == b && b * q == c && c * q == d && d * q == e) {
            int next = e;
            for (int i = 0; i < 5; ++i) {
                next *= q;
                cout << next << " ";
            }
            cout << endl;
            return;
        }
    }

    if (a + b == c && b + c == d && c + d == e) {
        int x = d, y = e;
        for (int i = 0; i < 5; ++i) {
            int z = x + y;
            cout << z << " ";
            x = y;
            y = z;
        }
        cout << endl;
        return;
    }

    int x = d, y = e;
    for (int i = 0; i < 5; ++i) {
        int z = x + y;
        cout << z << " ";
        x = y;
        y = z;
    }
    cout << endl;
}

int main() {
    int a, b, c, d, e;
    while (cin >> a >> b >> c >> d >> e) {
        if (a == 0 && b == 0 && c == 0 && d == 0 && e == 0) break;
        predictNext(a, b, c, d, e);
    }
    return 0;
}
```
