class Solution {
public:
    string addBinary(string a, string b) {
        int al=a.size(),bl=b.size();
        while(al<bl){
            a='0'+a;
            al++;
        }
        while(al>bl){
            b='0'+b;
            bl++;
        }
        int forward=0;
        for(int i=al-1;i>=0;i--){
            int temp=a[i]+b[i]-'0'-'0'+forward;
            forward=temp/2;
            a[i]=temp%2+'0';
        }
        if(forward>0){
            a='1'+a;
        }
        return a;
    }
};
```

