void selectsort(int arr[], int n) {
    int begin = 0;
    while (begin < n) {
        int min = INT_MAX; 
        int tag = begin; 

        for (int i = begin; i < n; i++) {
            if (arr[i] < min) {
                tag = i;
                min = arr[tag];
            }
        }
        int temp = arr[begin];
        arr[begin] = arr[tag];
        arr[tag] = temp;
        begin++; 
    }
